const crypto = require('crypto');
const wb = $('Webhook').first().json.body;
const geo = $input.first().json || {};

function sha256(val) {
  if (!val) return null;
  return crypto.createHash('sha256').update(String(val).trim().toLowerCase()).digest('hex');
}

const ts = new Date((wb.event_time || Math.floor(Date.now()/1000)) * 1000);
const sheetTs = ts.toISOString().replace('T', ' ').substring(0, 19);

// META CAPI PAYLOAD
const metaPayload = {
  data: [{
    event_name: "PageView",
    event_time: wb.event_time,
    event_id: wb.event_id,
    event_source_url: wb.source_url || "https://deenbuddy.vercel.app/quiz-funnel/",
    action_source: "website",
    user_data: {
      em: wb.email ? [sha256(wb.email)] : [],
      fbc: wb.fbc || null,
      fbp: wb.fbp || null,
      external_id: wb.browser_id ? [wb.browser_id] : [],
      client_ip_address: wb.client_ip || null,
      client_user_agent: wb.user_agent || null,
      ct: geo.city ? [sha256(geo.city)] : [],
      st: geo.regionName ? [sha256(geo.regionName)] : [],
      zp: geo.zip ? [sha256(geo.zip)] : [],
      country: geo.country ? [sha256(geo.country)] : [],
    },
    custom_data: {
      value: wb.value || 0,
      currency: "USD",
      content_name: "quiz_funnel",
      content_category: wb.avatar || "unknown",
    }
  }]
};

// SHEET ROW
const sheetRow = {
  session_id: wb.session_id,
  created_at: sheetTs,
  updated_at: sheetTs,
  status: "page_view",
  fbclid: wb.fbclid || null,
  fbc: wb.fbc || null,
  fbp: wb.fbp || null,
  utm_source: wb.utm_source || null,
  utm_medium: wb.utm_medium || null,
  utm_campaign: wb.utm_campaign || null,
  utm_content: wb.utm_content || null,
  utm_term: wb.utm_term || null,
  browser_id: wb.browser_id || null,
  client_ip: wb.client_ip || null,
  user_agent: wb.user_agent || null,
  source_url: wb.source_url || null,
  referrer: wb.referrer || null,
  country: geo.country || null,
  region: geo.regionName || null,
  city: geo.city || null,
  zip: geo.zip || null,
};

return [{ json: { meta_payload: metaPayload, sheet_row: sheetRow } }];
